
def triangle(a, b, c):
    if a == 0 or b == 0 or c == 0:
        return "invalid"
    lst = [a, b, c]
    max_value = max(lst)
    min_value = min(lst)
    x = lst.pop(lst.index(max_value))                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           